import React from 'react';

// Here we are using object destructuring assignment to pluck off our variables from the props object
// We assign them to their own variable names
function NavTabs({ currentPage, handlePageChange }) {
  return (
  <div>
    <header class="header">
      <h1>
        Marco's React Portfolio
      </h1>
      <nav>
        <ul className="header-ul">
          <li className="nav-item">
            <a
              href="#about"
              onClick={() => handlePageChange('About')}
              // This is a conditional (ternary) operator that checks to see if the current page is "About"
              // If it is, we set the current page to 'nav-link-active', otherwise we set it to 'nav-link'
              className={currentPage === 'About' ? 'nav-link active' : 'nav-link'}
            >
              About
            </a>
          </li>
          <li className="nav-item">
            <a
              href="#portfolio"
              onClick={() => handlePageChange('Portfolio')}
              // Check to see if the currentPage is `Portfolio`, and if so we use the active link class from bootstrap. Otherwise, we set it to a normal nav-link
              className={currentPage === 'Portfolio' ? 'nav-link active' : 'nav-link'}
            >
              Portfolio
            </a>
          </li>
          <li className="nav-item">
            <a
              href="#contact"
              onClick={() => handlePageChange('Contact')}
              // Check to see if the currentPage is `Contact`, and if so we use the active link class from bootstrap. Otherwise, we set it to a normal nav-link
              className={currentPage === 'Contact' ? 'nav-link active' : 'nav-link'}
            >
              Contact
            </a>
          </li>

          <li className="nav-item">
            <a
              href="#resume"
              onClick={() => handlePageChange('Resume')}
              // Check to see if the currentPage is `Resume`, and if so we use the active link class from bootstrap. Otherwise, we set it to a normal nav-link
              className={currentPage === 'Resume' ? 'nav-link active' : 'nav-link'}
            >
              Resume
            </a>
          </li>
        </ul>
      </nav>
    </header>

    <footer id="contact-me">
        <div class="contact-me-section">

            <ul>
                <li><a href="mailto:MarcoFlores1029@gmail.com">Contact Me</a></li>|
                <li><a href="https://github.com/JaggedComet?tab=repositories">Github</a></li>|
                <li><a href="https://www.linkedin.com/in/marco-flores-923602175/">Linkdin</a></li>|
            </ul>

        </div>
        <div class="copyright">
            <p>Marco Flores Copyright 2021</p>
        </div>
    </footer>

  </div>


  );
}

export default NavTabs;
